CREATE DATABASE CRUD_react_Django;
CREATE TABLE IF NOT EXISTS product (
   key_p int NOT NULL AUTO_INCREMENT, 
   pro_name varchar(90) DEFAULT NULL,     
   pro_provider varchar(50) DEFAULT NULL,     
   pro_existences int DEFAULT NULL,     
   pro_date date DEFAULT NULL,     
   pro_description varchar(400) DEFAULT NULL,     
   pro_category varchar(50) DEFAULT NULL,    
   createdAt timestamp NULL DEFAULT NULL,     
   updatedAt timestamp NULL DEFAULT NULL,     
   PRIMARY KEY (`key_p`) 
   );

   USE crud_react_django;



CREATE TABLE IF NOT EXISTS employees ( 
   em_id int NOT NULL AUTO_INCREMENT,     
   em_name varchar(50) DEFAULT NULL,     
   em_lastName varchar(50) DEFAULT NULL,
   em_phone int DEFAULT NULL,     
   en_stall VARCHAR(15),   
   PRIMARY KEY (`em_id`) 
   );
   